/**
 * Fishbowl Inventory BI Reports - Modern Theme (Bootstrap 5 Based)
 * Professional ocean blue theme with Bootstrap 5 integration
 *
 * Usage: Include Bootstrap 5.3+ CSS before this file
 * <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
 * <link href="fishbowl-theme-modern.css" rel="stylesheet">
 */

/* ============================================
   Bootstrap Variable Overrides
   Ocean Blue Professional Theme
   ============================================ */

:root {
    /* Primary Brand Colors - Ocean Blue Theme */
    --fb-primary: #2D9CDB;           /* Deep Ocean Blue */
    --fb-primary-light: #2D9CDB;     /* Light Ocean Blue */
    --fb-primary-dark: #0D47A1;      /* Dark Ocean Blue */
    --fb-primary-pale: #E3F2FD;      /* Very Light Blue Background */
    --fb-primary-soft: #BBDEFB;      /* Soft Blue for accents */

    /* Bootstrap Primary Override */
    --bs-primary: #1565C0;
    --bs-primary-rgb: 21, 101, 192;

    /* Secondary Colors */
    --fb-secondary: #00ACC1;
    --fb-secondary-light: #26C6DA;
    --fb-secondary-dark: #00838F;
    --bs-secondary: #00ACC1;
    --bs-secondary-rgb: 0, 172, 193;

    /* Status Colors */
    --fb-success: #2E7D32;
    --fb-success-light: #43A047;
    --fb-success-bg: #E8F5E9;
    --bs-success: #2E7D32;
    --bs-success-rgb: 46, 125, 50;

    --fb-warning: #F57C00;
    --fb-warning-light: #FF9800;
    --fb-warning-bg: #FFF3E0;
    --bs-warning: #F57C00;
    --bs-warning-rgb: 245, 124, 0;

    --fb-danger: #C62828;
    --fb-error: #C62828;
    --fb-danger-light: #E53935;
    --fb-error-bg: #FFEBEE;
    --bs-danger: #C62828;
    --bs-danger-rgb: 198, 40, 40;

    --fb-info: #1976D2;
    --fb-info-light: #42A5F5;
    --fb-info-bg: #E3F2FD;
    --bs-info: #1976D2;
    --bs-info-rgb: 25, 118, 210;

    /* Neutral Scale */
    --fb-gray-50: #FAFAFA;
    --fb-gray-100: #F5F5F5;
    --fb-gray-200: #EEEEEE;
    --fb-gray-300: #E0E0E0;
    --fb-gray-400: #BDBDBD;
    --fb-gray-500: #9E9E9E;
    --fb-gray-600: #757575;
    --fb-gray-700: #616161;
    --fb-gray-800: #424242;
    --fb-gray-900: #212121;

    /* Background & Border */
    --bs-body-bg: #FAFAFA;
    --bs-body-color: #212121;
    --bs-border-color: #E0E0E0;
    --bs-border-radius: 0.375rem;

    /* Typography */
    --bs-body-font-family: 'Open Sans', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, sans-serif;
    --bs-body-font-size: 0.9375rem;  /* 15px - not too large */
    --bs-body-font-weight: 400;
    --bs-body-line-height: 1.6;

    /* Headings */
    --bs-heading-color: var(--fb-primary-dark);

    /* Shadows */
    --bs-box-shadow-sm: 0 0.125rem 0.25rem rgba(0, 0, 0, 0.075);
    --bs-box-shadow: 0 0.25rem 0.5rem rgba(0, 0, 0, 0.1);
    --bs-box-shadow-lg: 0 0.5rem 1rem rgba(0, 0, 0, 0.15);
}

/* ============================================
   Base Overrides
   ============================================ */

body {
    background-color: var(--fb-gray-50);
    font-family: var(--bs-body-font-family);
}

h1, h2, h3, h4, h5, h6,
.h1, .h2, .h3, .h4, .h5, .h6 {
    color: var(--fb-primary-dark);
    font-weight: 600;
}

h1, .h1 { font-size: 1.75rem; }    /* 28px */
h2, .h2 { font-size: 1.5rem; }     /* 24px */
h3, .h3 { font-size: 1.25rem; }    /* 20px */
h4, .h4 { font-size: 1.125rem; }   /* 18px */
h5, .h5 { font-size: 1rem; }       /* 16px */
h6, .h6 { font-size: 0.875rem; }   /* 14px */

/* ============================================
   Enhanced Button Styles
   ============================================ */

.btn {
    font-weight: 600;
    font-size: 0.875rem;
    padding: 0.5rem 1.25rem;
    transition: all 0.2s ease;
}

.btn:hover {
    transform: translateY(-1px);
    box-shadow: 0 0.25rem 0.5rem rgba(0, 0, 0, 0.15);
}

.btn:active {
    transform: translateY(0);
}

.btn-primary {
    background-color: var(--fb-primary);
    border-color: var(--fb-primary);
}

.btn-primary:hover {
    background-color: var(--fb-primary-dark);
    border-color: var(--fb-primary-dark);
}

.btn-success {
    background-color: var(--fb-success);
    border-color: var(--fb-success);
}

.btn-success:hover {
    background-color: var(--fb-success-light);
    border-color: var(--fb-success-light);
}

.btn-sm {
    font-size: 0.8125rem;
    padding: 0.375rem 1rem;
}

.btn-lg {
    font-size: 1rem;
    padding: 0.625rem 1.5rem;
}

/* ============================================
   Cards
   ============================================ */

.card {
    border: 1px solid var(--fb-gray-300);
    box-shadow: var(--bs-box-shadow-sm);
    transition: box-shadow 0.2s ease;
}

.card:hover {
    box-shadow: var(--bs-box-shadow);
}

.card-header {
    background-color: var(--fb-primary-pale);
    border-bottom: 1px solid var(--fb-gray-300);
    font-weight: 600;
    color: var(--fb-primary-dark);
    padding: 0.875rem 1.25rem;
}

.card-body {
    padding: 1.25rem;
}

.card-footer {
    background-color: var(--fb-gray-100);
    border-top: 1px solid var(--fb-gray-300);
}

/* Compact Card Variant */
.card-compact .card-body {
    padding: 0.875rem;
}

.card-compact .card-header {
    padding: 0.625rem 0.875rem;
    font-size: 0.875rem;
}

/* ============================================
   Accordion (for collapsible sections)
   ============================================ */

.accordion-button {
    font-weight: 600;
    color: var(--fb-primary-dark);
    background-color: var(--fb-gray-100);
    font-size: 0.9375rem;
}

.accordion-button:not(.collapsed) {
    background-color: var(--fb-primary-pale);
    color: var(--fb-primary);
    box-shadow: none;
}

.accordion-button:focus {
    box-shadow: 0 0 0 0.2rem rgba(21, 101, 192, 0.15);
}

.accordion-item {
    border: 1px solid var(--fb-gray-300);
    margin-bottom: 0.5rem;
}

.accordion-body {
    padding: 1rem 1.25rem;
}

/* ============================================
   Alerts
   ============================================ */

.alert {
    border-left: 4px solid;
    font-size: 0.875rem;
}

.alert-success {
    background-color: var(--fb-success-bg);
    color: var(--fb-success);
    border-color: var(--fb-success);
}

.alert-danger {
    background-color: var(--fb-error-bg);
    color: var(--fb-danger);
    border-color: var(--fb-danger);
}

.alert-warning {
    background-color: var(--fb-warning-bg);
    color: var(--fb-warning);
    border-color: var(--fb-warning);
}

.alert-info {
    background-color: var(--fb-info-bg);
    color: var(--fb-info);
    border-color: var(--fb-info);
}

/* ============================================
   Badges
   ============================================ */

.badge {
    font-weight: 600;
    font-size: 0.75rem;
    padding: 0.35em 0.65em;
}

/* Status Badge Pills */
.badge-pill {
    border-radius: 1rem;
}

/* ============================================
   Tables
   ============================================ */

.table {
    font-size: 0.875rem;
    --bs-table-bg: white;
}

.table thead th {
    background-color: var(--fb-primary);
    color: white;
    font-weight: 600;
    border-bottom: 2px solid var(--fb-primary-dark);
    padding: 0.875rem 0.75rem;
    font-size: 0.8125rem;
    text-transform: uppercase;
    letter-spacing: 0.025em;
}

.table-striped tbody tr:nth-of-type(odd) {
    background-color: var(--fb-primary-pale);
}

.table-hover tbody tr:hover {
    background-color: var(--fb-primary-soft);
    cursor: pointer;
}

/* Compact Table */
.table-sm thead th,
.table-sm tbody td {
    padding: 0.5rem 0.5rem;
    font-size: 0.8125rem;
}

/* Sticky Header */
.table-sticky thead th {
    position: sticky;
    top: 0;
    z-index: 10;
}

/* Cell Highlighting */
.table .cell-error {
    background-color: #ffcdd2 !important;
    border: 2px solid var(--fb-danger);
}

.table .cell-warning {
    background-color: #ffe0b2 !important;
    border: 2px solid var(--fb-warning);
}

/* ============================================
   Forms & Inputs
   ============================================ */

.form-control,
.form-select {
    font-size: 0.875rem;
    border-color: var(--fb-gray-400);
}

.form-control:focus,
.form-select:focus {
    border-color: var(--fb-primary);
    box-shadow: 0 0 0 0.2rem rgba(21, 101, 192, 0.15);
}

.form-label {
    font-weight: 600;
    color: var(--fb-gray-700);
    font-size: 0.875rem;
    margin-bottom: 0.375rem;
}

/* Filter Panel Style */
.filter-panel {
    padding: 1rem;
    background-color: var(--fb-primary-pale);
    border: 2px solid var(--fb-primary-light);
    border-radius: var(--bs-border-radius);
}

.form-check-input:checked {
    background-color: var(--fb-primary);
    border-color: var(--fb-primary);
}

/* ============================================
   Modals
   ============================================ */

.modal-header {
    background-color: var(--fb-primary-pale);
    border-bottom: 2px solid var(--fb-primary);
}

.modal-title {
    color: var(--fb-primary);
    font-weight: 600;
}

.modal-footer {
    background-color: var(--fb-gray-100);
}

/* ============================================
   Toasts
   ============================================ */

.toast {
    font-size: 0.875rem;
    box-shadow: var(--bs-box-shadow);
}

.toast-header {
    font-weight: 600;
    background-color: var(--fb-primary-pale);
    color: var(--fb-primary-dark);
}

/* ============================================
   Offcanvas (Side Panels)
   ============================================ */

.offcanvas-header {
    background-color: var(--fb-primary);
    color: white;
}

.offcanvas-title {
    font-weight: 600;
}

.offcanvas-body {
    padding: 1.25rem;
}

/* ============================================
   Progress Bars
   ============================================ */

.progress {
    height: 1.25rem;
    background-color: var(--fb-gray-200);
}

.progress-bar {
    background-color: var(--fb-primary);
    transition: width 0.3s ease;
}

/* ============================================
   Loading Spinner
   ============================================ */

.spinner-border-primary {
    color: var(--fb-primary);
}

/* Custom Loading Overlay */
.loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(255, 255, 255, 0.95);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    z-index: 9999;
}

.loading-overlay .spinner-border {
    width: 3rem;
    height: 3rem;
    border-width: 0.3rem;
    color: var(--fb-primary);
}

/* ============================================
   Custom Components - Summary Stats
   ============================================ */

.stat-card {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.625rem 1.125rem;
    border-radius: 1.25rem;
    font-size: 0.875rem;
    font-weight: 600;
    color: white;
    box-shadow: var(--bs-box-shadow-sm);
    transition: transform 0.2s ease;
}

.stat-card:hover {
    transform: translateY(-2px);
    box-shadow: var(--bs-box-shadow);
}

.stat-card.total {
    background: linear-gradient(135deg, var(--fb-info), var(--fb-info-light));
}

.stat-card.valid {
    background: linear-gradient(135deg, var(--fb-success), var(--fb-success-light));
}

.stat-card.warning {
    background: linear-gradient(135deg, var(--fb-warning), var(--fb-warning-light));
}

.stat-card.error {
    background: linear-gradient(135deg, var(--fb-danger), var(--fb-danger-light));
}

.stat-number {
    font-size: 1.25rem;
    font-weight: 700;
}

.stat-label {
    font-size: 0.8125rem;
    opacity: 0.95;
}

/* ============================================
   Custom Components - Upload Section
   ============================================ */

.upload-area {
    padding: 2rem;
    background-color: var(--fb-primary-pale);
    border: 3px dashed var(--fb-primary);
    border-radius: var(--bs-border-radius);
    text-align: center;
    transition: all 0.3s ease;
    cursor: pointer;
}

.upload-area:hover {
    border-color: var(--fb-primary-dark);
    background-color: var(--fb-primary-soft);
    transform: translateY(-2px);
}

.upload-area.dragover {
    border-color: var(--fb-success);
    background-color: var(--fb-success-bg);
    border-style: solid;
}

.upload-icon {
    font-size: 3rem;
    color: var(--fb-primary);
    margin-bottom: 0.5rem;
}

/* ============================================
   Custom Components - Issue Groups
   ============================================ */

.issue-group {
    margin-bottom: 0.875rem;
    padding: 1rem;
    background: white;
    border-radius: var(--bs-border-radius);
    border-left: 4px solid var(--fb-danger);
    box-shadow: var(--bs-box-shadow-sm);
}

.issue-group.warning-group {
    border-left-color: var(--fb-warning);
}

.issue-group-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 0.75rem;
    flex-wrap: wrap;
    gap: 0.75rem;
}

.issue-group-title {
    font-weight: 600;
    color: var(--fb-gray-900);
    font-size: 0.9375rem;
}

.issue-count {
    padding: 0.25rem 0.75rem;
    background-color: var(--fb-danger);
    color: white;
    border-radius: 1rem;
    font-size: 0.75rem;
    font-weight: 700;
}

.issue-group.warning-group .issue-count {
    background-color: var(--fb-warning);
}

.issue-items {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
    margin-bottom: 0.75rem;
}

.issue-value {
    padding: 0.375rem 0.75rem;
    background-color: var(--fb-gray-100);
    border: 1px solid var(--fb-gray-300);
    border-radius: 0.875rem;
    font-size: 0.75rem;
    color: var(--fb-gray-800);
    font-family: 'Courier New', monospace;
}

/* ============================================
   Custom Components - Column Reference Grid
   ============================================ */

.column-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 0.875rem;
    margin-top: 1rem;
}

.column-item {
    padding: 0.875rem;
    background: white;
    border: 1px solid var(--fb-gray-300);
    border-radius: var(--bs-border-radius);
    font-size: 0.8125rem;
    transition: all 0.2s ease;
}

.column-item:hover {
    border-color: var(--fb-primary);
    box-shadow: var(--bs-box-shadow-sm);
}

.column-item .col-name {
    font-weight: 700;
    color: var(--fb-primary-dark);
    font-size: 0.875rem;
}

.column-item .col-type {
    color: var(--fb-gray-600);
    font-size: 0.75rem;
    margin-left: 0.5rem;
    font-style: italic;
}

.column-item .col-required {
    color: var(--fb-danger);
    font-weight: 700;
    margin-left: 0.375rem;
    font-size: 0.75rem;
}

.column-item .col-desc {
    color: var(--fb-gray-600);
    margin-top: 0.375rem;
    font-size: 0.75rem;
    line-height: 1.4;
}

/* ============================================
   Utility Classes
   ============================================ */

/* Text Colors */
.text-fb-primary { color: var(--fb-primary) !important; }
.text-fb-success { color: var(--fb-success) !important; }
.text-fb-warning { color: var(--fb-warning) !important; }
.text-fb-danger { color: var(--fb-danger) !important; }
.text-fb-info { color: var(--fb-info) !important; }

/* Background Colors */
.bg-fb-primary { background-color: var(--fb-primary) !important; }
.bg-fb-primary-pale { background-color: var(--fb-primary-pale) !important; }
.bg-fb-success { background-color: var(--fb-success) !important; }
.bg-fb-warning { background-color: var(--fb-warning) !important; }
.bg-fb-danger { background-color: var(--fb-danger) !important; }
.bg-fb-info { background-color: var(--fb-info) !important; }

/* Border Colors */
.border-fb-primary { border-color: var(--fb-primary) !important; }
.border-fb-success { border-color: var(--fb-success) !important; }
.border-fb-warning { border-color: var(--fb-warning) !important; }
.border-fb-danger { border-color: var(--fb-danger) !important; }

/* Spacing Utilities (complement Bootstrap) */
.gap-sm { gap: 0.5rem; }
.gap-md { gap: 1rem; }
.gap-lg { gap: 1.5rem; }

/* Font Weights */
.fw-semibold { font-weight: 600; }

/* Animations */
@keyframes fadeIn {
    from { opacity: 0; transform: translateY(10px); }
    to { opacity: 1; transform: translateY(0); }
}

.fade-in {
    animation: fadeIn 0.3s ease;
}

/* ============================================
   Responsive Adjustments
   ============================================ */

@media (max-width: 768px) {
    .stat-card {
        flex: 1 1 calc(50% - 0.5rem);
        min-width: 140px;
    }

    .column-grid {
        grid-template-columns: 1fr;
    }

    .table {
        font-size: 0.75rem;
    }

    h1, .h1 { font-size: 1.5rem; }
    h2, .h2 { font-size: 1.25rem; }
}

/* ============================================
   Utility Classes
   ============================================ */

.hidden {
    display: none !important;
}

/* File Upload */
#fileInput {
    display: none;
}

#fileName {
    margin-top: 8px;
    font-weight: bold;
    color: var(--fb-primary);
    font-size: 13px;
}

/* Expandable Row Details */
.expand-btn {
    background: none;
    border: none;
    cursor: pointer;
    padding: 4px 8px;
    font-size: 12px;
    color: var(--fb-primary);
    transition: color 0.2s;
}

.expand-btn:hover {
    color: var(--fb-primary-dark);
    text-decoration: underline;
}

.row-details {
    display: none;
}

.row-details.expanded {
    display: table-row;
}

.row-details td {
    padding: 12px;
    background-color: var(--fb-gray-50);
    border-top: 2px solid var(--fb-primary);
}

/* ============================================
   Print Styles
   ============================================ */

@media print {
    .btn, .offcanvas, .modal, .toast {
        display: none !important;
    }

    .card {
        box-shadow: none;
        border: 1px solid #000;
    }

    .table thead th {
        background-color: #f0f0f0 !important;
        color: #000 !important;
        -webkit-print-color-adjust: exact;
        print-color-adjust: exact;
    }
}
